mapper.update:
update sys_user
set last_login = #{lastLogin}
where id = #{id}

mapper.select:
select u.id, u.username, u.nickname, u.email, u.phone, sd.name as deptName, u.avatar
from sys_user u
    left join sys_dept sd on sd.id = u.dept_id
    left join sys_dept_type sdt on sdt.code = sd.type_code
where u.status = #{status}
  and sd.status = #{deptStatus}
  and sdt.status = #{deptTypeStatus}
  and (u.username like #{keyword} or nickname like #{keyword} or sd.name like #{keyword})

  and sd.id = #{deptId}
